<?phpnamespace App;use Illuminate\Notifications\Notifiable;use Illuminate\Foundation\Auth\User as Authenticatable;class CustomPage extends Authenticatable{    use Notifiable;    protected $table = 'custom_page';    protected $fillable = [ 'title', 'contents', 'slug', 'type' ];    protected $hidden = [];    const TYPE_CONTACT = 1;    const TYPE_PURCHASE = 2;    const TYPE_SELLER = 3;    const TYPE_PAYMENT = 4;    static $page_type = [    	self::TYPE_CONTACT => 'Về chúng tôi',    	self::TYPE_PURCHASE => 'Dành cho người mua',    	self::TYPE_SELLER => 'Dành cho người bán',    	self::TYPE_PAYMENT => 'Phương thức thanh toán'    ];}